const twitter = require('./lib/scraper');

async function go(url) {
    // console.log(await getHtml('https://twitter.com/anmolgarg_'));
    const html = await twitter.getHtml(url);
    const twtcount = await twitter.getTwitterFollowers(html);
    console.log('you have ' + twtcount + ' followers');
}


module.exports = (str) => {
    return go(str);
}